#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent #The $basedir variable is then set to the directory of the script 

$exe="" #The $exe variable is set to an empty string. This is later used to append .exe to the node
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0 #The $ret variable is set to 0.
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../jsesc/bin/jsesc" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../jsesc/bin/jsesc" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../jsesc/bin/jsesc" $args
  } else {
    & "node$exe"  "$basedir/../jsesc/bin/jsesc" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
